import React, { useEffect, useState } from 'react';
import tidal from '../api/tidal'

const ProfileInfo = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    try {
      tidal.get('/get_user_picture').then(res => setData(res.data))
    } catch (err) {
      console.error(err)
    }
  }, [])

  if (data) {
    const picture = data.picture
    const sourceString = picture.replace(/-/g, '/')
    const src = `https://resources.tidal.com/images/${sourceString}/100x100.jgpg`
    return src
  }

  return (
    <div>
      <h1>Welcome, {data.firstName}!</h1>
      <img src={src} />
    </div>

  )

}

export default ProfileInfo;
