import React, { useState, useEffect } from 'react';

import tidal from '../api/tidal';

const Albums = ({ selectedValue }) => {
  const [data, setData] = useState([])

  useEffect(() => {
    try {
      if (selectedValue !== null) {
        tidal.get(`/search_artist_albums/${selectedValue.id}`).then(res => setData(res.data))
      }
    } catch (err) {
      console.error(err)
    }
  }, [selectedValue])


  return (
    <div className='albums'>
      <h1 className='p-4 text-3xl font-bold underline'>Albums</h1>
      <div className='grid grid-rows-3'>
        {data.albums && (
          data.albums.map((album, idx) =>
            <div>
              <p key={idx}>{album.name}</p>
              <img src={album.picture} key={idx} />
            </div>
          )
        )}
      </div>
    </div>
  )
}

export default Albums;
